UPDATE `locales_quest` SET `Title_loc2` = 'Pyro-côtelettes de chimaerok à la Dirge' ,`Details_loc2` = 'Ma recette est au point ! Je m’occupe des épices, et vous m’avez déjà fourni la viande. J’ai juste besoin de carburant pour fusée et de sel de Fonderoc.$B$BLorsque vous m’aurez ramené tout ça, le poulet sera prêt pour Narain.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'C’est prêt ! Je vous présente la plus grande de mes créations : les pyro-côtelettes de chimaerok à la Dirge !'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 8586;
UPDATE `locales_quest` SET `Title_loc2` = 'La liste des affectations' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'In the old days I would\'ve marked your forehead with the creature\'s blood to mark your accomplishment here. &nbsp;Thrall insists we comply with the Druids\' requests.$B$BHere, take these papers and go back to Cenarion Hold. &nbsp;I\'m sure they will have suitable work for you.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 8732;
UPDATE `locales_quest` SET `Title_loc2` = 'Les maraudeurs du Crépuscule' ,`Details_loc2` = 'Les sectateurs du Marteau du crépuscule disposent de troupes de cavalerie. Elles chevauchent hors de portée des forces basées au Fort cénarien, attaquent nos caravanes et s’en prennent aux plus faibles de nos patrouilles.$B$BTrouvez et tuez ces maraudeurs du Crépuscule, en particulier leur chef, une redoutable guerrière du nom de Morna. Lorsque vous aurez terminé, allez faire votre rapport à l’imploratrice céleste Corne-fière.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Well done, &lt;name&gt;. &nbsp;The twilight marauders were inflicting an unacceptable number of casualties on our patrols. &nbsp;Here\'s your next assignment.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 8740;
UPDATE `locales_quest` SET `Title_loc2` = 'Des renforts d\'armure pour le champ de bataille' ,`Details_loc2` = 'Les réserves de renforts d’armures de la brigade de Forgefer sont dangereusement basses. Procurez-vous-en, et livrez-les à Janela Rudemartel, à l’avant-poste de la brigade de Forgefer, près de la Ruche’Zora.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Thanks, I\'ll hand these out to the lads.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 8780;
