UPDATE `locales_quest` SET `Title_loc2` = 'Les garde-mains de rédemption' ,`Details_loc2` = 'Vous trouverez les restes de l’équipement de nos héros tombés au combat sur les cadavres des seigneurs de Naxxramas. Rapportez-moi ces souvenirs désacralisés ainsi que des débris d’armures et des matériaux d’artisanat capables de revitaliser les pièces d’armures maudites. Ils me permettront de vous confectionner une superbe protection.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9048;
UPDATE `locales_quest` SET `Title_loc2` = 'Les bottes de traqueur des cryptes' ,`Details_loc2` = 'J’ai découvert un moyen d’utiliser les carapaces des démons des cryptes. En combinant ces fragments avec des restes d’armures désacralisées perdues dans Naxxramas, je peux façonner une armure mortelle et extrêmement légère.$B$BApportez-moi les éléments nécessaires et je vous fabriquerai une armure d’un genre encore inconnu dans notre monde !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9058;
