UPDATE `locales_quest` SET `Title_loc2` = 'La tour a intérêt à prendre garde' ,`Details_loc2` = 'Et vous, abattez-moi cette tour !' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9065;
UPDATE `locales_quest` SET `Title_loc2` = 'Les bottes de Brise-terre' ,`Details_loc2` = 'Me ferez-vous l’honneur de me rapporter les armures de ceux qui sont tombés en défendant ces terres ?$B$BLes armures ont été désacralisées et sont sans nul doute utilisées à mauvais escient, mais en combinant ces pièces désacralisées avec d’autres débris d’armures datant de la guerre, on peut les renouveler et en faire des armures efficace pour les batailles à venir.$B$BApportez-moi les restes d’armure de nos disparus et quelques ingrédients de purification, et vous pourrez porter l’armure de Brise-terre.$B$BCherchez dans Naxxramas.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9072;
UPDATE `locales_quest` SET `Title_loc2` = 'Les protège-poignets de Brise-terre' ,`Details_loc2` = 'Me ferez-vous l’honneur de me rapporter les armures de ceux qui sont tombés en défendant ces terres ?$B$BLes armures ont été désacralisées et sont sans nul doute utilisées à mauvais escient, mais en combinant ces pièces désacralisées avec d’autres débris d’armures datant de la guerre, on peut les renouveler et en faire des armures efficace pour les batailles à venir.$B$BApportez-moi les restes d’armure de nos disparus et quelques ingrédients de purification, et vous pourrez porter l’armure de Brise-terre.$B$BCherchez dans Naxxramas.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9075;
UPDATE `locales_quest` SET `Title_loc2` = 'La cuirasse de la faucheuse d\'os' ,`Details_loc2` = 'Si vous voulez que je fabrique de la faucheuse d’os, vous allez devoir me fournir les ingrédients.$B$BEntrez dans Naxxramas, la grande nécropole flottante qu’on voit là-haut dans le ciel, et trouvez des débris d’armures détruites pendant la guerre et des morceaux d’armures désacralisées. Ramenez-moi ça avec les ingrédients pour les assembler et vous aurez votre faucheuse d’os. Au fait, il faudra aussi me payer pour le dérangement... c’est moi qui fournis les os.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9077;
UPDATE `locales_quest` SET `Title_loc2` = 'Les cuissards de la faucheuse d\'os' ,`Details_loc2` = 'Si vous voulez que je fabrique de la faucheuse d’os, vous allez devoir me fournir les ingrédients.$B$BEntrez dans Naxxramas, la grande nécropole flottante qu’on voit là-haut dans le ciel, et trouvez des débris d’armures détruites pendant la guerre et des morceaux d’armures désacralisées. Ramenez-moi ça avec les ingrédients pour les assembler et vous aurez votre faucheuse d’os. Au fait, il faudra aussi me payer pour le dérangement... c’est moi qui fournis les os.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9078;
UPDATE `locales_quest` SET `Title_loc2` = 'Le casque de la faucheuse d\'os' ,`Details_loc2` = 'Si vous voulez que je fabrique de la faucheuse d’os, vous allez devoir me fournir les ingrédients.$B$BEntrez dans Naxxramas, la grande nécropole flottante qu’on voit là-haut dans le ciel, et trouvez des débris d’armures détruites pendant la guerre et des morceaux d’armures désacralisées. Ramenez-moi ça avec les ingrédients pour les assembler et vous aurez votre faucheuse d’os. Au fait, il faudra aussi me payer pour le dérangement... c’est moi qui fournis les os.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9079;
