UPDATE `locales_quest` SET `Title_loc2` = 'Le ceinturon de marcherêve' ,`Details_loc2` = 'Bien des héros sont tombés pendant la dernière guerre, et nombreux sont ceux qui ont péri depuis face au Fléau. Les armures arrachées à leurs cadavres ont été emportées à Naxxramas dans un but qui nous échappe.$B$BRécupérez les armures désacralisées et autres débris d’armures, fournissez-moi les autres ingrédients dont j’ai besoin, et je vous confectionnerai une armure de marcherêve.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9092;
UPDATE `locales_quest` SET `Title_loc2` = 'Naxxramas, la citadelle de l\'effroi' ,`Details_loc2` = 'Dans les profondeurs de Pestebois se trouve l\'entrée de la citadelle de l\'effroi, Naxxramas. Toutes les tentatives pour y pénétrer ont échoué en raison des balises magiques placées sur le portail runique - jusqu\'à présent.$B$BNous avons mis au point un moyen d\'entrer grâce à une sorte d\'occultation arcanique permanente, un vieux sortilège du Kirin Tor avec quelques modifications personnelles. Cela étant dit, l\'occultation est coûteuse. Mais puisque votre dévouement à la cause de l\'Aube est admirable, nous sommes prêts à prendre en charge un partie des dépenses.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You will feel a slight tingle, &lt;name&gt;. That sensation means it\'s working. Once complete, you will be able to enter Naxxramas by stepping upon the rune portal in Plaguewood.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9122;
UPDATE `locales_quest` SET `Title_loc2` = 'Armer un cuirassier' ,`Details_loc2` = 'Avez-vous déjà vu une armure de cuirassier, $C ? Non, bien sûr que non, vous n’êtes encore &lt;qu’un gamin/qu’une gamine&gt;, un vulgaire moutard. Alors, laissez-moi vous dire une chose : c’est magnifique.$B$BVous vous demandez ce que cela a à voir avec vous, hein ? Je vais vous le dire, &lt;le/la môme. J’ai besoin de très nombreux morceaux de sombrefer, et je suis prêt à les payer.$B$BIls me serviront à créer des ensembles d’armures de cuirassier pour nos champions. Alors, ramenez-moi des morceaux et passez à la caisse.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'This will do, for now. If you get more, you know  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  to find me.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9131;
UPDATE `locales_quest` SET `Title_loc2` = 'On m\'appelle « Le Coq »' ,`Details_loc2` = 'Vous voulez une commission d\'artisan ? Quoi ? Vous vous imaginez que je vais vous donner une de ces commissions si vivement recherchées juste parce que vous vous êtes &lt;montré/montrée&gt; ici ? Sûrement pas, feignasse. Des artisans du monde entier cherchent du travail ici sur le front.$B$BSi vous voulez une commission, vous allez devoir me montrer que vous êtes &lt;prêt/prête&gt; à verser du sang pour la cause. Apportez-moi une marque de valeur et nous reparlerons.$B$BEt pour mettre vos petites mains poisseuses de &lt;bon à rien/bonne à rien sur une de ces marques, vous n\'avez qu\'une chose à faire - commencez le carnage.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Alright, here\'s how it works: For every valor token that you turn in, I\'ll give you another writ. You complete the order on that writ and deliver the order to Packmaster Stonebruiser. Got it? Good. Now get out of here.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9141;
UPDATE `locales_quest` SET `Title_loc2` = 'Commission d\'artisan - Pantalons runiques en cuir' ,`Details_loc2` = 'Le porteur de la présente commission est en droit de recevoir comme paiement l’insigne de son choix à la livraison des articles ci-dessous.$B$BBon de commande TC-8485 :$B$B4 pantalons runiques en cuir.$B$BToute commission honorée doit être livrée au maître-fourrier Navrepierre, à la chapelle de l’Espoir de Lumière dans les Maleterres de l’est.$B$BLe présent contrat est nul et non avenu s’il est modifié ou endommagé.$B$B-Commandant d’ordonnance Metz, Aube d’argent' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9187;
UPDATE `locales_quest` SET `Title_loc2` = 'Commission d\'artisan - Poissons cuirassés' ,`Details_loc2` = 'Le porteur de la présente commission est en droit de recevoir comme paiement l’insigne de son choix à la livraison des articles ci-dessous.$B$BBon de commande PC-12 :$B$B30 poissons cuirassés.$B$BToute commission honorée doit être livrée au maître-fourrier Navrepierre, à la chapelle de l’Espoir de Lumière dans les Maleterres de l’est.$B$BLe présent contrat est nul et non avenu s’il est modifié ou endommagé.$B$B-Commandant d’ordonnance Metz, Aube d’argent' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You really don\'t know what these are for?$B$B&lt;Packmaster Stonebruiser laughs.&gt;$B$BLike I\'m going to tell you!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9205;
UPDATE `locales_quest` SET `Title_loc2` = 'La Garde-glace' ,`Details_loc2` = 'Ah, la Garde-glace. C’est une fine couche de magie protectrice qui recouvre certaines pièces d’équipement. Ceux qui se battent contre les créatures de glace ou de givre gagnent une protection améliorée contre leurs attaques en utilisant la Garde-glace.$B$BComme pour chaque chose que fabrique la Voix du courroux, il y a un prix à payer... Un prix qui ne baissera pas, même si vous passez la journée à geindre ! Alors, ça vous intéresse ?' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9211;
UPDATE `locales_quest` SET `Title_loc2` = 'Armes de bataille épiques - Révéré auprès de l\'Aube' ,`Details_loc2` = 'Salutations, $Gami:amie. Avez-vous des insignes de l\'Aube ou de la Croisade que vous aimeriez échanger contre des objets d\'une grande puissance ?$B$BIl faut que je vous prévienne : les articles épiques achetés lorsqu\'on est révéré auprès de l\'Aube sont encore très chers. Je vous conseille d\'améliorer votre réputation auprès de l\'Aube avant d\'aller plus avant dans vos projets.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9225;
UPDATE `locales_quest` SET `Title_loc2` = 'Armes de bataille excellentes - Exalté auprès de l\'Aube' ,`Details_loc2` = 'Salutations, $Gami:amie. Avez-vous des insignes de l\'Aube ou de la Croisade que vous aimeriez échanger contre des objets puissants ?$B$BVous bénéficiez d\'une remise sur les objets proposés à ceux qui sont exaltés auprès de l\'Aube. Je vous encourage à faire un achat !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Good day, &lt;name&gt;. It was a pleasure doing business with you.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9227;
UPDATE `locales_quest` SET `Title_loc2` = 'Les brassards polaires' ,`Details_loc2` = 'Grâce à vous et Omarion (qu’il repose en paix), je suis maintenant capable de confectionner des brassards polaires. Vous n\'avez plus qu\'à m\'apporter les matériaux... et à me verser une modeste rétribution.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9241;
UPDATE `locales_quest` SET `Title_loc2` = '40 bons - Schéma : Commande de chariotte à vapeur' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Here you go, here you go! &nbsp;A prize fit for a king! &nbsp;Congratulations to you, my friend!$B$BIf you have more tickets you\'d like to turn in, then please let me know! &nbsp;For as long as the Darkmoon Faire is here, I\'ll redeem your tickets.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9249;
