UPDATE `locales_quest` SET `Title_loc2` = 'Faire de la place' ,`Details_loc2` = 'On a des tonnes de marchandises à livrer à l\'auberge de Kharanos. Il faut que ça arrive vite, donc je vais vous laisser prendre mon bélier de course. Je vous serais déjà reconnaissant si vous effectuez juste une livraison, mais je vous donne cinq minutes, donc essayez d\'en faire le plus possible et je vous récompenserai d\'autant plus.' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 11347;
