UPDATE `locales_quest` SET `Title_loc2` = 'Un entraînement de pompier' ,`Details_loc2` = 'Les braves nains de Kharanos sont prêts à organiser une brigade de pompiers à tout instant, maintenant que le Cavalier sans tête rôde par ici. Voulez-vous nous apporter votre aide ?$B$BVers le sud, les montagnards ont préparé un coin pour que l’on puisse s’entraîner à éteindre les incendies. Allez-y, et lancez des seaux sur les flammes. Vous pouvez remplir votre seau au baril d’eau, ici.$B$BAu revoir, $N. Quand vous aurez terminé votre entraînement, revenez nous voir !' ,`Objectives_loc2` = 'Jetez des seaux d’eau sur 5 feux, puis revenez parler à la Directrice d\'orphelinat costumée.' ,`OfferRewardText_loc2` = 'Vous êtes trempée et couverte de suie ! Vous devez avoir éteint plusieurs feux !$B$BBien joué ! Maintenant que vous êtes un membre honoraire de la brigade des pompiers, je vous supplie de revenir ici si jamais le village a besoin de vous !'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 11439;
UPDATE `locales_quest` SET `Title_loc2` = 'Un entraînement de pompier' ,`Details_loc2` = 'Avec la menace constante du Cavalier sans tête, les gardes-paix se sont empressés de mettre en place une brigade de pompiers en cas de besoin. Voulez-vous nous apporter votre aide ?$B$BÀ l’ouest, vers l’Exodar, les gardes-paix ont installé une petite zone d’entraînement à la lutte contre les incendies. Allez-y, et lancez des seaux sur les flammes. Vous pouvez remplir votre seau au baril d’eau, ici.$B$BAu revoir, $N. Quand vous aurez terminé votre entraînement, revenez nous voir !' ,`Objectives_loc2` = 'Jetez des seaux d’eau sur 5 feux, puis revenez parler à la Directrice d\'orphelinat costumée.' ,`OfferRewardText_loc2` = 'You\'re wet and smudged with soot! &nbsp;You must have been extinguishing fires!$B$BWell done! &nbsp;As an honorary member of the Fire Brigade, I beg you to return here if ever the village needs you!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 11440;
UPDATE `locales_quest` SET `Title_loc2` = 'La meilleure des bières' ,`Details_loc2` = '$GUn p’tit gars futé:Une gamine futée comme vous va vouloir faire cesser les hostilités en surface, non ? Vous aimeriez que votre gentille petite fête des Brasseurs se déroule sans accroc, alors passons un accord.$B$BApportez ce tonneau de notre meilleure bière à l’organisateur de la fête, à la sortie de Forgefer, pour que les nains Sombrefer soient correctement représentés ! La meilleure bière de tout Azeroth vient de cette montagne, allez pas l’oublier !' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 11486;
