UPDATE `locales_quest` SET `Title_loc2` = 'La tablette de Leviroth' ,`Details_loc2` = 'Vous frottez les inscriptions sur la tablette, à la recherche de mots que vous pourriez comprendre. Vous finissez par trouver le fragment suivant :$B$B"Les fils du brouillard prendront mille âmes pour celui qui habite les eaux les plus sombres. Alors son émissaire se réveillera."$B$BKaruk sera sûrement intéressé par cette information.' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 11621;
