UPDATE `locales_quest` SET `Title_loc2` = 'Cartes de navigation (périmée)' ,`Details_loc2` = 'Vous avez découvert la première partie d’une carte de navigation dessinée sur une peau de dragon tannée. Si vous parveniez la complèter, vous pourriez découvrir les prochains plans d’attaque des kvaldir.$B$BD’autres marins des Courcinglants pourraient avoir les cartes manquantes sur eux. Ces informations intéresseront Karuk.' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 11992;
