UPDATE `locales_quest` SET `Title_loc2` = 'RÉUTILISER' ,`Details_loc2` = 'La brigade de la marche de l’Ouest a été expédiée dans la région nord-est des Grisonnes pour sécuriser le passage de nos troupes. Ils ont installé un camp de base au nord-est de Grisegueule, soit au sud-ouest de Thor Modan.$B$BVous reconnaîtrez peut-être le chef de la brigade, Gryan Roidemantel, un vétéran honoré de la guerre contre le Fléau en Lordaeron. Pour les gens de la marche de l’Ouest, il est surtout renommé pour sa décision de quitter le champ de bataille et de rentrer dans ses foyers pour combattre les défias.' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 11997;
UPDATE `locales_quest` SET `Title_loc2` = 'zzOLDTarot de chasse : Fauves mineurs' ,`Details_loc2` = 'Lorsque vous regardez la carte, l’image se brouille puis vous vous voyez $Gcouvert:couverte de nombreuses fourrures de loups et d’ours. Vous ne pouvez que supposer que ce sera votre destin, si vous invoquez le pouvoir de la carte de tarot.' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 12001;
