UPDATE `locales_quest` SET `Title_loc2` = 'La vengeance de Veehja' ,`Details_loc2` = 'Nous avons un ennemi commun aujourd\'hui, $R ! Mon peuple a été anéanti par ces kvaldirs... Ma ville a été pratiquement rasée. L\'heure de la vengeance a sonné !$B$BEn échange de mon aide, je voudrais que vous tuiez les monstres barbares qui défigurent les Courcinglants. Vous serez bien $Grécompensé:récompensée, $C.' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 12490;
UPDATE `locales_quest` SET `Title_loc2` = 'Test PvP' ,`Details_loc2` = 'Faut tuer des gars d\'en face. Sont méchants.' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 12493;
UPDATE `locales_quest` SET `Title_loc2` = 'La foudre frappe vraiment deux fois' ,`Details_loc2` = 'D\'ac, les trucs de cristal...$B$BLes grosses-langues utilisent les cristaux pour donner pluie à leurs sanctuaires. Zepik sait pas bien comment fonctionne, mais Zepik sait que si elles utilisent mauvais cristaux, nuages et pluie devenir fous !$B$BZepik essaye d\'entrer dans un de leurs autres sanctuaires de pluie, mais pas bien marcher. Marche mieux à deux !$B$BNous prend cristaux, nous place au sanctuaire, puis nous détruit cristaux pour libérer puissance. Zepik pas sûr quoi se passer, alors meilleur recule après.' ,`Objectives_loc2` = 'Détruisez les Grappes de cristaux des Confins-du-ciel, en face du Sanctuaire du temps Murmebrume, pour le compte de Zepik.$B$BSi vous perdez le Cor de chasse de Zepik, allez parler à l\'Ancien Harkek à la Colline de Frénécœur.' ,`OfferRewardText_loc2` = 'Ça marche trop bien !$B$BVous voit éclair frapper l\'oracle ? Trop bon !$B$B&lt;Zepik se frotte les mains et glousse comme un maniaque.&gt;'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 12537;
UPDATE `locales_quest` SET `Title_loc2` = 'Dominer Achérus' ,`Details_loc2` = 'Contemplez Achérus, la majestueuse citadelle de la mort ! Elle n\'a pas d\'égale en ce monde. Même la puissante Naxxramas ne pourrait résister à un assaut du fort d\'Ébène.$B$BVos pensées sont peut-être encore confuses, mais nous n\'avons pas un instant à perdre. Vous devez déclarer notre domination sur Achérus et sur votre esprit ! Les cors de guerre retentissent !$B$BPrenez les escaliers à cet étage, au nord, au sud, à l\'est et à l\'ouest, qui vous mèneront à l\'étage de la garnison, au-dessus de nous. Cherchez le maître poliorcète Corvus et demandez-lui son rapport. Il doit être remis à Thalanor.' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 12625;
UPDATE `locales_quest` SET `Title_loc2` = 'La compagnie des damnés' ,`Details_loc2` = '$GCorvus vous salue.&gt;$B$BNos réserves d\'armures, d\'armes et de munitions auront bientôt atteint leur capacité maximale. Nous sommes prêts pour la guerre !$B<br :>&lt;Corvus roule un long morceau de parchemin à la texture malsaine.$B$BVous trouverez les quartiers des officiers de l\'autre côté de l\'étage de la garnison, au sud-ouest. Prenez ce rapport et remettez-le au commandant du Fléau Thalanor, le commandant du bataillon de la Compagnie des damnés.' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 12626;
