UPDATE `locales_quest` SET `Title_loc2` = 'PÉRIMÉ&gt;&gt;L\'ennemi de notre ennemi' ,`Details_loc2` = 'Même si nous abhorrons travailler avec les chevaliers de la mort, nous ne pouvons faire la fine bouche face à nos alliés.$B$BNous sommes coincés entre le Fléau et les sauvages Drakkari, qui ne demandent rien de plus que de nous détruire, pour s\'atteindre les uns les autres.$B$BDirigez-vous vers l\'ouest jusqu\'au guet d\'Ébène. Parlez à Stefan et voyez s\'il a besoin de vos services.' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 12780;
