UPDATE `locales_quest` SET `Title_loc2` = 'Faites-moi partir ce marchand !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 14097;
UPDATE `locales_quest` SET `Title_loc2` = 'La relique du Cercle terrestre' ,`Details_loc2` = 'Vous avez fait de gros progrès, $N. Maintenant que vous maîtrisez les quatre éléments de base, vous devez apprendre à les équilibrer. Il y a un avantage à cela : avec le temps, vous n’aurez plus besoin que d’une seule relique au lieu de vos quatre totems.$B$BPour perfectionner votre maîtrise des éléments, allez parler à Gotura Quatre-vents, dans la vallée de la Sagesse d’Orgrimmar.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Ah, you\'ve mastered the four totems. You should be proud of your accomplishments so far, young $R, but this is only the beginning of your journey.$B$BHere, accept this gift from the Earthen Ring. Carry this relic with you and you will have the power of all four elements at your command.$B$BContinue your training, friend. Grave challenges are ahead for this world - I can hear it in the wind and feel it in the rumblings of the earth. We must be prepared for the worst.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 14100;
UPDATE `locales_quest` SET `Title_loc2` = 'La relique du Cercle terrestre' ,`Details_loc2` = 'Vous avez fait de gros progrès, $N. Maintenant que vous maîtrisez les quatre éléments de base, vous devez apprendre à les équilibrer. Il y a un avantage à cela – avec le temps, vous n’aurez plus besoin que d’une seule relique au lieu de vos quatre totems.$B$BAllez parler au long-voyant Eanuu, près de la Grande forge de Forgefer. Il vous aidera à perfectionner votre maîtrise des éléments.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'I see you\'ve mastered use of the four totems. You are on your way to becoming one with the elemental spirits, but this is only the beginning of your journey.$B$BHere, accept this gift from the Earthen Ring. Carry this relic with you and you will have the power of all four elements at your command.$B$BYour training must continue! I sense a great many trials lie ahead for this world, and we will need the power of the elements behind us if we are to prevail.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 14111;
UPDATE `locales_quest` SET `Title_loc2` = 'Les morts reconnaissants' ,`Details_loc2` = 'C’est bien agréable de pouvoir rendre visite à sa famille et à ses amis. Mais je crois que j’ai peut-être un peu exagéré… J’ai déjà mangé tout ce qu’ils avaient préparé pour moi ! $B$BPourrais-je vous demander un peu plus de ce délicieux pain des morts ? Je vous en serais très reconnaissant…$B$BJe crois que Chapman, là-bas, en vend la recette.' ,`Objectives_loc2` = 'Récupérez un peu de Pain des morts, et donnez-le à l\'Esprit de troll joyeux dans le cimetière d\'Orgrimmar.$B$BUtilisez un Chrysanthème orange pour voir l’esprit, qui sinon restera invisible.' ,`OfferRewardText_loc2` = 'Ha, encore de ce délicieux pain des morts ! Merci.$B$BTenez, prenez ceci... Cela vous divertira pendant les festivités.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 14177;
UPDATE `locales_quest` SET `Title_loc2` = 'Kickoff Mail Marker' ,`Objectives_loc2` = '<h2 class="clear">Informations connexes</h2>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 14200;
UPDATE `locales_quest` SET `Title_loc2` = 'Tuez des Sectateurs/Dragons' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 14298;
UPDATE `locales_quest` SET `Title_loc2` = 'La cuite de Liam' ,`Details_loc2` = 'Allez-vous-en ! Vous êtes dans ma tête... Vous n\'êtes pas $Gréel/réelle&gt;... Vous êtes &lt;mort/morte&gt; !$B$BVous avez péri dans Gilnéas... les worgens, ils vous ont &lt;eu/eue&gt;...$B$BÇa aurait dû être moi...$B$BAhhh... d\'abord le sol qui n\'arrête pas de bouger... et maintenant ces visions... Trop de gnôle...$B<br :>&lt;Le prince a l\'air complètement ivre. Peut-être devriez-vous chercher un autre interlocuteur.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 14315;
UPDATE `locales_quest` SET `Title_loc2` = 'Tremblements' ,`Details_loc2` = '$N ! Nous pensions ne plus jamais vous revoir !$B$BÉcoutez, nous avons besoin de votre aide. Nous avons subi un tremblement de terre impressionnant et les répliques ne sont pas encore terminées. Par ailleurs, le prince Liam ne nous sera pas très utile.$B$BNous devons nous assurer que nos défenses tiennent le coup. La Porte de la Reine est la seule protection qui nous sépare d\'une horde de worgens enragés venus de la forêt Noire, l\'écume aux babines. Dites-moi que la Porte a tenu le coup.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 14317;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'évacuation' ,`Details_loc2` = 'Nous avons survécu par pure chance, $N. Mais notre bonne étoile nous abandonne.$B$BLa côte est submergée alors même que nous discutons. Et d\'après ce que je peux voir, la terre sous nos pieds ne s\'est pas encore stabilisée.$B$BJ\'ai horreur d\'avoir à le dire, mais nous devons partir. Malédiction, $N... Je ne peux tout de même pas perdre ma terre natale une seconde fois en une seule vie. Mais ces séismes sont des ennemis que nous ne pouvons combattre.$B$BDites à Gwen Armstead de commencer l\'évacuation.' ,`Objectives_loc2` = 'Parlez à Gwen Armstead à Havre-du-Soir.' ,`OfferRewardText_loc2` = 'Liam a raison. Nous devons emmener les gens sur les hauteurs.$B$BIl faut que vous m\'aidiez. Annoncez la nouvelle pendant que je m\'occupe des préparatifs de l\'évacuation.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 14397;
UPDATE `locales_quest` SET `Title_loc2` = 'Les nécrotraqueurs' ,`Details_loc2` = 'La lettre de recommandation devrait être suffisante pour convaincre Andron que vous êtes $Gle messager:la messagère qu\'il attendait. J\'ai déjà rencontré Andron, il n\'est pas assez intelligent pour être soupçonneux.$B$BNotre chef suspecte Andron d\'avoir fourni de l\'aide à un autre groupe. Il exige de savoir lequel et en veut la preuve.$B$BJe veux que vous alliez voir Andron pour obtenir cette information. Vous le trouverez à son magasin près de l\'Apothicarium.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 14419;
UPDATE `locales_quest` SET `Title_loc2` = 'Les nécrotraqueurs' ,`Details_loc2` = 'Vous trouverez les informations qui intéressent vos employeurs dans le grand livre sur l\'étagère derrière moi. Apportez-leur avec mes respects et dites-leur que je préférerais que ce soit vous qu\'ils envoient, si ce qu\'ils trouvent leur semble avoir assez de valeur, pour continuer notre petit accord.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 14421;
UPDATE `locales_quest` SET `Title_loc2` = 'La première quête' ,`Details_loc2` = 'Bonjour $N, ceci est un test. Acceptez-la et tuez des trucs.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 14425;
UPDATE `locales_quest` SET `Title_loc2` = 'La seconde quête' ,`Details_loc2` = 'Ceci est la deuxième quête ! Cette fois, vous n’aurez pas à tuer de trucs.' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 14426;
