UPDATE `locales_quest` SET `Title_loc2` = 'Appel aux armes : Œil du cyclone' ,`Details_loc2` = 'Nous nous battrons et nous écraserons nos ennemis où que nous les trouvions. Azeroth, l’Outreterre, ça ne fait aucune différence.$B$BNous ne pouvons pas laisser l’Alliance établir une supériorité dans l’Œil du cyclone. Allez leur livrer bataille dès aujourd’hui... demain il sera peut-être trop tard !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 24222;
UPDATE `locales_quest` SET `Title_loc2` = 'Hors service' ,`Details_loc2` = 'Hé, $C, je veux que vous me répariez le cataclysme. Allez donc chasser quelques zombies.' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 24460;
UPDATE `locales_quest` SET `Title_loc2` = 'Ne nous oubliez pas' ,`Details_loc2` = 'Vous nous quittez ? Pensez à faire le plein de provisions pour le voyage, avant de partir.$B$BMais surtout, n’oubliez pas de parler à Tharek Noirepierre, une fois à Kharanos. Dites-lui ce qui se passe ici, dans les Frigères, et demandez-lui de nous envoyer de l’aide. J’espère qu’il pourra nous sortir de cette situation.' ,`Objectives_loc2` = 'Rendez-vous auprès de Tharek Noirepierre, à Kharanos.' ,`OfferRewardText_loc2` = 'La vallée des Frigères ? Mais j’ai des problèmes plus importants, ici ! Vous n’allez pas me dire qu’ils n’arrivent même pas à se débarrasser de quelques troggs ! Ni d’une bande de trolls ! Ni à repousser les incursions des élémentaires !!$B$BEuh, tout bien réfléchi, ça fait quand même pas mal de problèmes. Je suis surpris qu’ils s’en sortent aussi bien face à tous ces ennemis. Je vais voir ce que je peux faire.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 24493;
