UPDATE `locales_quest` SET `Title_loc2` = 'Match de sélection' ,`Details_loc2` = '$N, Sainturrébreutel, l\'entraîneur des intrépides Boucaniers de Baille-Fonds, vous cherche.$B$BIl m\'a dit que le titre de la division allait bientôt être disputé contre les Requins de Gentepression et que vous êtes $Gle seul:la seule qui puisse nous le rapporter !$B$BAllez en ville, vers l\'ouest. Impossible de passer à côté du parc des Princes marchands sans le voir !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 24521;
UPDATE `locales_quest` SET `Title_loc2` = 'Quête de test de Paul' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 24544;
UPDATE `locales_quest` SET `Title_loc2` = 'Des aventures archéologiques' ,`Details_loc2` = 'Hé, vous voulez goûter mes nouveaux hot-dogs au dimeurtrodon ? Non ? Du risotto de raptor non plus ? Des ptacos au pterreurdactyle, alors ?$B$BD’accord. Oh, ça me revient, maintenant ! J’ai un ami dans le coin, qui pourrait avoir besoin de votre aide. Il s’appelle Étincelle Minerien et c’est le président-archéologue général des Expéditions Marshal.$B$BIl a été envoyé aux sources de Golakka, à l’ouest. Vous le trouverez sûrement sur la rive occidentale des sources.' ,`Objectives_loc2` = 'Faites votre rapport à Étincelle Minerien, aux Sources de Golakka.' ,`OfferRewardText_loc2` = 'Sacré vieux Nolen. Comment va-t-il ? Mieux que moi, j\'espère…'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 24698;
UPDATE `locales_quest` SET `Title_loc2` = 'Le journal de Williden' ,`Details_loc2` = 'Vous avez trouvé un journal abîmé. Certaines pages sont encore lisibles, et vous déchiffrez qu’un certain Williden Marshal revendique la propriété de son contenu. Si la carte qui s’y trouve dessinée est correcte, Williden se trouve quelque part au milieu du cratère. Selon le journal, ses compagnons et lui ont voulu adopter une attitude plus offensive après l’attaque de leur camp.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 24738;
