UPDATE `locales_quest` SET `Title_loc2` = 'Revirement' ,`Objectives_loc2` = '<h3 class="first">Progrès</h3>' ,`OfferRewardText_loc2` = 'May you find what you\'re looking for, $Gbrother:sister.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 24843;
