UPDATE `locales_quest` SET `Title_loc2` = 'Racines de feuillebleue' ,`Details_loc2` = 'Les racines de feuillebleue sont un mets très recherché de par le monde, mais elles sont rares. Très rares ! Le seul endroit où on en trouve est ici, dans les Tarides, loin sous la surface, dans le kraal de Tranchebauge.$B$BEt même si vous savez où les trouver, elles sont presque impossibles à repérer si vous ne savez pas où regarder ! C’est la raison pour laquelle j’ai dressé ces sniffetarins. Ils ont un odorat surdéveloppé et peuvent sentir une racine de feuillebleue à cinquante pas.$B$BCe ne sera pas facile, mais si vous me trouvez quelques racines, je vous paierai grassement.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 24900;
UPDATE `locales_quest` SET `Title_loc2` = 'Dame Noire, veille sur nous' ,`Details_loc2` = 'En fait, pourquoi n’iriez-vous pas voir directement dame Sylvanas? La présence dans nos rangs d’un autre mage formé par le Kirin Tor est un évènement qui mérite une audience.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 24987;
