UPDATE `locales_quest` SET `Title_loc2` = 'DRAPEAU - Murloc violet' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 25071;
UPDATE `locales_quest` SET `Title_loc2` = 'Dompter la bête' ,`Details_loc2` = 'Récemment, nous avons observé des ours pestiférés franchir les montagnes qui nous séparent des Maleterres. Bien que les excursions de ces ours en Tirisfal mettent à mal nos défenses à la Barricade, leur passage est une bénédiction pour nous autres chasseurs.$B$BProuvez-moi que vous avez retenu quelque chose de notre dernière leçon, et utilisez ce bâtonnet sur un kodiak pestiféré, loin à l\'ouest d\'ici.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 25077;
UPDATE `locales_quest` SET `Title_loc2` = 'La formation de dresseur' ,`Details_loc2` = 'Une dernière chose, $Gchasseur:chasseresse. Vous voudrez probablement parler à Apolos, à Fossoyeuse. Il vous donnera les dernières instructions à connaître pour prendre soin et entraîner votre familier.$B$BVous le trouverez dans le quartier de la Guerre.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 25083;
UPDATE `locales_quest` SET `Title_loc2` = 'Le chef de guerre a besoin de vous' ,`Details_loc2` = 'Fidèle à sa parole, Thrall va nous mener sur la voie de la libération de notre peuple. Il a demandé à vous parler.$B$BBonne chance, $N. Nous comptons tous sur vous.' ,`Objectives_loc2` = 'Parlez à Thrall au Poste de guet du Chef de guerre, sur les Îles Perdues.' ,`OfferRewardText_loc2` = 'Une fois encore, vous nous avez aidés à vaincre notre ennemi commun, l\'Alliance.$B$BIl est maintenant temps pour nous de vous aider.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 25098;
UPDATE `locales_quest` SET `Title_loc2` = 'Haro sur l\'éclaireur' ,`Details_loc2` = 'Les Sombrefers sont sur le point de nous jouer un tour pendable, comme d’habitude. Ils se tiennent cois, mais je sens que quelque chose se prépare.$B$BNous allons mener l’enquête. Un éclaireur sombrefer batifole près du centre de ce qui s’appelait autrefois le loch, sans doute avec ordre de nous espionner. Je veux que vous l’éliminiez et que vous me rapportiez tout document ou ordre en sa possession.$B$BSi mes soupçons se révèlent infondés… je prendrai mes responsabilités en subissant moi-même le blâme de Moira. C’est juré.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 25117;
UPDATE `locales_quest` SET `Title_loc2` = 'Cibles d\'opportunité' ,`Details_loc2` = 'Ashlan va certainement vous envoyer dans le loch pour que vous surveilliez les vandales sombrefers dont il nous rabâche les oreilles. Tant que vous y êtes, vous n’avez qu’à vous faire un peu d’argent, non ? J’ai pas raison ?$B$BL’assèchement du lac a fait disparaître presque toute trace de vie. Il n’y reste plus beaucoup d’eau pour nager, mais des crocilisques s’attardent encore sur le sol asséché.$B$BIls finiront par mourir de faim, donc autant les tuer tant qu’ils sont encore bien frais. Rapportez-moi des flancs de crocilisque et on parlera gros sous.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 25119;
UPDATE `locales_quest` SET `Title_loc2` = 'Dans les ombres' ,`Details_loc2` = 'Je veux que vous essayiez de passer devant moi en étant $Gcamouflé:camouflée. Comme vous êtes encore novice, je serai encore capable de vous voir. Pas de blagues, hein ? Je veux juste voir ce que vous avez appris.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 25142;
UPDATE `locales_quest` SET `Title_loc2` = 'Thonk' ,`Details_loc2` = '&lt;Oh mon chou, je viens de me souvenir/Désolée si j’ai été un peu dure. Parfois&gt; je suis un poil jalouse lorsque d’autres femmes arrivent en ville. Je ne suis pas très sûre de moi, au fond.;$B$BMon très bon ami Thonk travaille à la tour de garde, là-haut, sur les hauteurs juste au nord-ouest de Tranchecolline. Il a dit un truc à propos d’un fleuve qui aurait débordé, de beaucoup de boulot à faire… Je n’écoutais pas, pour être franche… J’étais fascinée par ses splendides yeux marrons.$B$B&lt;Ah non, tu ne vas pas te mettre à être jaloux, hein ?/Merci.' ,`Objectives_loc2` = 'Présentez-vous à Thonk, à la tour de garde au nord-ouest de Tranchecolline.' ,`OfferRewardText_loc2` = 'C’est Gail qui vous envoie? Est-ce que… est-ce qu’elle vous a parlé de moi ?'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 25227;
UPDATE `locales_quest` SET `Title_loc2` = 'Ak\'Zeloth' ,`Details_loc2` = 'Depuis la dernière grande guerre où la Légion ardente fut défaite, j\'ai recherché les sources de corruption démoniaque dans la société des orcs. Le collier que vous m\'avez apporté confirme mes craintes.$B$BIl appartient à la Lame ardente.$B$BVotre rôle dans cette histoire est presque achevé, $N. Allez au poste de Guet-Lointain, à l\'ouest sur la frontière des Tarides et parlez avec mon assistant, Ak\'Zeloth. Expliquez-lui ce que je viens de vous dire.' ,`Objectives_loc2` = 'Parlez à Ak\'Zeloth au Poste de Guet-Lointain, dans les Tarides du Nord.' ,`OfferRewardText_loc2` = 'Je sais déjà tout cela. J\'apprécie vos efforts, mais la Lame ardente devra attendre. Une autre affaire requiert notre attention.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 25264;
UPDATE `locales_quest` SET `Title_loc2` = 'Les vestiges de la bataille' ,`Details_loc2` = 'Une grande bataille a eu lieu ici. Parmi les squelettes de nagas, vous remarquez ce qui ressemble à des cadavres de grands humanoïdes. Ça pourrait intéresser XXX.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 25302;
