UPDATE `locales_quest` SET `Title_loc2` = 'Détrôner la Royale' ,`Details_loc2` = 'Ce grand livre est plein de noms de lieux, et j’ai besoin de vous pour enquêter et agir. Il est temps de mettre le holà à toute cette histoire.$B$BTenez, prenez cette mention : on dirait que c’est à Uldum, juste à l’est de la tombe de Khartut, près de l’entrée venant de Tanaris. Allez-y et défoncez-les.$B$BEt si vous voyez quelque chose d’assez gros et d’assez dangereux pour mériter d’être éparpillé façon puzzle, lancez-lui cette bombe.' ,`Objectives_loc2` = 'Tuez 5 Laquais de La Royale et utilisez la Bombe en khorium de Tordboulon pour endommager un Chariot de produits chimiques à l’est de la Tombe de Khartut, dans Uldum, puis retournez voir l\'Inspecteur Latenaille Tordboulon à Hurlevent.' ,`OfferRewardText_loc2` = 'Beau travail, petit. Vous méritez un petit quelque chose. Hé, revenez demain si vous avez le temps ; j\'aurai peut-être un travail pour vous.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 28934;
UPDATE `locales_quest` SET `Title_loc2` = 'Détrôner la Royale' ,`Details_loc2` = 'Ce grand livre est plein de noms de lieux, et j’ai besoin de vous pour enquêter et agir. Il est temps de mettre le holà à toute cette histoire.$B$BTenez, prenez cette mention : on dirait que c’est à Uldum, juste à l’est de la tombe de Khartut, près de l’entrée venant de Tanaris. Allez-y et défoncez-les.$B$BEt si vous voyez quelque chose d’assez gros et d’assez dangereux pour mériter d’être éparpillé façon puzzle, lancez-lui cette bombe.' ,`Objectives_loc2` = 'Tuez 5 Laquais de La Royale et utilisez la Bombe en khorium de Tordboulon pour endommager un Chariot de produits chimiques à l’est de la Tombe de Khartut, dans Uldum, puis retournez voir l\'Inspecteur Lapince Tordboulon à Hurlevent.' ,`OfferRewardText_loc2` = 'Beau travail, petit. Vous méritez un petit quelque chose. Hé, revenez demain si vous avez le temps ; j\'aurai peut-être un travail pour vous.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 28935;
UPDATE `locales_quest` SET `Title_loc2` = 'Alliance Breadcrumb' ,`Details_loc2` = 'Des monstres ! C\'étaient des monstres, que je vous dis !$B$BNotre navire a jeté l\'ancre juste au sud de Stromgarde, et la côte ici est infestée d\'étranges créatures.$B$BJ\'ai juste aperçu leur sale tronche et entendu des gargouillements bizarres, et je suis parti sans demander mon reste. Vous devriez vraiment aller aider mes camarades de bord, $N!' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 28937;
